// Login.js
import { AuthContext } from 'contexts/AuthContext';
import React, { useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';


const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const history = useHistory();
  const { login } = useContext(AuthContext);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleLogin = async (e) => {
  e.preventDefault();
  try {
    // Make an API call to the backend to authenticate the user and get the JWT token
    const response = await fetch('http://localhost:8080/api/v1/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      const { token } = await response.json();
      login(token); // Update the authentication state with the received token
      history.push('/admin/dashboard'); // Redirect to the dashboard after successful login
    } else {
      // Handle login error (e.g., show an error message)
      const errorResponse = await response.json();
      console.error('Login failed:', errorResponse.message);
      // Implement logic to display an error message to the user (e.g., using a toast notification)
    }
  } catch (error) {
    // Handle login error (e.g., show an error message)
    console.error('Login failed:', error);
    // Implement logic to display an error message to the user (e.g., using a toast notification)
  }
};


  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
