import React, { useState, useEffect, useContext } from "react";
import PropTypes from "prop-types";
import createApiInstance from "api/api";
import { AuthContext } from "contexts/AuthContext";
import CardAddPatient from "./CardAddPatient";

export default function CardPatients({ color }) {
  const { token } = useContext(AuthContext);
  const [searchQuery, setSearchQuery] = useState("");
  const [patients, setPatients] = useState([]);
  const [filteredPatients, setFilteredPatients] = useState([]);
  const [isAddPatientOpen, setIsAddPatientOpen] = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null); // New state for the selected patient
  const [showNotification, setShowNotification] = useState(false); // State for showing notification
  const apiInstance = createApiInstance(token);

  useEffect(() => {
    apiInstance
      .get("api/patients")
      .then((response) => {
        setPatients(response.data);
        showNotification("Patient ajouté avec success", "success");
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    setFilteredPatients(patients);
  }, [patients]);

  useEffect(() => {
    const query = searchQuery.toLowerCase();
    const filtered = patients.filter((patient) => {
      const { cin, nom, prenom } = patient;
      return (
        cin.toLowerCase().includes(query) ||
        nom.toLowerCase().includes(query) ||
        prenom.toLowerCase().includes(query)
      );
    });
    setFilteredPatients(filtered);
  }, [searchQuery, patients]);

  const handleDeletePatient = (patientId) => {
    const confirmDelete = window.confirm(
      "Êtes-vous sûr de vouloir supprimer ce patient ?"
    );
    if (confirmDelete) {
      apiInstance
        .delete(`/api/patients/${patientId}`)
        .then((response) => {
          console.log(response.data);
          setFilteredPatients((prevPatients) =>
            prevPatients.filter((patient) => patient.id_patient !== patientId)
          );
        })
        .catch((error) => {
          console.error(error);
        });
    }
  };

  const handleOpenAddPatient = () => {
    setIsAddPatientOpen(true);
  };

  const handleCloseAddPatient = () => {
    setIsAddPatientOpen(false);
  };

  const handleAddPatientSuccess = () => {
    setIsAddPatientOpen(false);
    apiInstance
      .get("api/patients")
      .then((response) => {
        setPatients(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleShowNotification = (patient) => {
    setSelectedPatient(patient);
    setShowNotification(true);

    setTimeout(() => {
      setShowNotification(false);
      setSelectedPatient(null);
    }, 3000);
  };
  showNotification = (message, type) => {
    setNotificationMessage(message);
    setNotificationType(type);

    setTimeout(() => {
      setNotificationMessage("");
      setNotificationType("");
    }, 3000);
  };

  

  return (
    <div>
      {isAddPatientOpen ? (
        <CardAddPatient
          onClose={handleCloseAddPatient}
          onAddSuccess={handleAddPatientSuccess}
        />
      ) : (
        <div
          className={
            "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
            (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
          }
        >
          <div className="rounded-t mb-0 px-4 py-3 border-0 text-center">
            <div className="flex justify-between items-center">
              <h3 className="text-xl font-bold lg:w-1/12">Patients</h3>
              <div className="flex items-center ">
                <button onClick={handleOpenAddPatient} className="mr-4">
                  <i className="fas fa-plus"></i>
                </button>
                <div className="relative flex items-center">
                  <span className="z-10 h-full leading-snug font-normal absolute text-blueGray-300 bg-transparent rounded text-base items-center justify-center w-8 pl-3 py-3">
                    <i className="fas fa-search"></i>
                  </span>
                  <input
                    type="text"
                    placeholder="Chercher ici"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="border-0 px-3 py-3 pl-10 placeholder-blueGray-300 text-blueGray-600 relative bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring"
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="block w-full overflow-x-auto">
            <table className="items-center w-full bg-transparent border-collapse">
              <thead>
                <tr>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  >
                    CIN
                  </th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  >
                    Nom et prénom
                  </th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  >
                    Date de naissance
                  </th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  >
                    Vérifié
                  </th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  ></th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  ></th>
                  <th
                    className={
                      "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-center " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  ></th>
                </tr>
              </thead>
              <tbody>
                {filteredPatients.map((patient) => (
                  <tr key={patient.id_patient}>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      {patient.cin || "-"}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      {patient.nom && patient.prenom
                        ? `${patient.nom} ${patient.prenom}`
                        : "-"}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      {patient.date_de_naissance || "-"}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      {patient.verifie === 0 && (
                        <i
                          className="fas fa-circle mr-2 text-lg"
                          style={{ color: "red", marginRight: "8px" }}
                        ></i>
                      )}
                      {patient.verifie === 1 && (
                        <i
                          className="fas fa-circle mr-2 text-lg"
                          style={{ color: "orange", marginRight: "8px" }}
                        ></i>
                      )}
                      {patient.verifie === 2 && (
                        <i
                          className="fas fa-circle mr-2 text-lg"
                          style={{ color: "green", marginRight: "8px" }}
                        ></i>
                      )}
                    </td>
                    <td className="border-t-0 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      <i className="fas fa-calendar-plus mr-2 text-lg"></i>
                    </td>
                    <td className="border-t-0 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      <i className="fas fa-eye mr-2 text-lg"></i>
                    </td>
                    <td className="border-t-0 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-center">
                      <button
                        className="focus:outline-none"
                        onClick={() => handleDeletePatient(patient.id_patient)}
                      >
                        <i className="fas fa-trash-alt mr-2 text-lg text-red-500"></i>
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          {showNotification && (
            <div className="fixed bottom-0 right-0 mb-4 mr-4 bg-blue-500 text-white py-2 px-4 rounded-md shadow-lg">
              {selectedPatient && (
                <>
                  <span className="mr-2">
                    Patient {selectedPatient.nom} {selectedPatient.prenom} ajouté avec succès!
                  </span>
                  <button
                    className="text-white font-semibold"
                    onClick={() => setShowNotification(false)}
                  >
                    Fermer
                  </button>
                </>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

CardPatients.defaultProps = {
  color: "light",
};

CardPatients.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};

CardAddPatient.propTypes = {
  onClose: PropTypes.func.isRequired,
  onAddSuccess: PropTypes.func.isRequired,
};
