import React, { useContext, useEffect, useState } from "react";

// components

import CardPatients from "components/Cards/CardPatients.js";
import { Redirect } from "react-router-dom/cjs/react-router-dom.min";
import createApiInstance from "api/api";
import { AuthContext } from "contexts/AuthContext";

export default function GestionPatient() {
  const { token } = useContext(AuthContext);
  const [patients, setPatients] = useState([]);
  const apiInstance = createApiInstance(token);

  useEffect(() => {
    apiInstance
      .get("api/patients")
      .then((response) => {
        setPatients(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleDeletePatient = (id_patient) => {
    apiInstance
      .delete(`api/patient/${id_patient}`)
      .then((response) => {
        setPatients((prevPatients) => prevPatients.filter((patient) => patient.id_patient !== id_patient));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  if (!token) {
    return <Redirect to="/auth/login" />;
  }

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full mb-12 px-4">
        <CardPatients color="light" patients={patients} onDelete={handleDeletePatient} />
        </div>
      </div>
    </>
  );
}
