// api.js

import axios from "axios";
import { useContext } from "react";
import { AuthContext } from "contexts/AuthContext"; // Replace this with the correct path to your AuthContext file

const apiInstance = axios.create({
  baseURL: "http://localhost:8080/api/v1/auth/userinfo",
});

apiInstance.interceptors.request.use(
  (config) => {
    // Get the token from the AuthContext
    const { token } = useContext(AuthContext); // Replace AuthContext with the actual context object you have defined in your application
    
    // Add the token to the request headers
    if (token) {
      config.headers["Authorization"] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export default apiInstance;
