import React, { useState } from 'react';
import { format, addDays, startOfMonth, endOfMonth, eachDayOfInterval, getDay } from 'date-fns';
import Modal from 'react-modal';
import "./Card.css"

const CardCalendar = () => {
    const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const firstDayOfMonth = startOfMonth(currentDate);
  const lastDayOfMonth = endOfMonth(currentDate);
  const daysInMonth = eachDayOfInterval({ start: firstDayOfMonth, end: lastDayOfMonth });

  const handlePrevMonth = () => {
    setCurrentDate(addDays(firstDayOfMonth, -1)); // Change to your logic for previous month
  };

  const handleNextMonth = () => {
    setCurrentDate(addDays(lastDayOfMonth, 1)); // Change to your logic for next month
  };

  const handleDayClick = (date) => {
    setSelectedDate(date);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  const handleSubmitForm = (e) => {
    e.preventDefault();
    // Here, you can implement the logic to send the appointment details to the backend
    console.log('Appointment details:', selectedDate, e.target.patientName.value);
    setShowForm(false);
  };

  const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  // Create an array to represent the calendar weeks
  const calendarWeeks = [];
  let currentWeek = [];
  let loopDate = firstDayOfMonth; // Rename the loopDate variable to avoid redeclaration

  // Iterate through the days of the month to create the calendar weeks
  while (loopDate <= lastDayOfMonth) {
    currentWeek.push(loopDate);

    // If we reach the end of the week, start a new week
    if (getDay(loopDate) === 6) {
      calendarWeeks.push(currentWeek);
      currentWeek = [];
    }

    loopDate = addDays(loopDate, 1);
  }
  return (
    <div className="calendar-container">
    <div className="calendar-header">
      <button onClick={handlePrevMonth}>Previous Month</button>
      <span>{format(firstDayOfMonth, 'MMMM yyyy')}</span>
      <button onClick={handleNextMonth}>Next Month</button>
    </div>
    <table className="calendar-table">
      <thead>
        <tr>
          {dayNames.map((dayName) => (
            <th key={dayName}>{dayName}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {calendarWeeks.map((week, weekIndex) => (
          <tr key={weekIndex}>
            {dayNames.map((_, dayIndex) => {
              const day = week[dayIndex];
              const dayNumber = day ? format(day, 'd') : '';
              const isCurrentMonth = day && day.getMonth() === firstDayOfMonth.getMonth();
              return (
                <td
                  key={dayIndex}
                  className={isCurrentMonth ? 'current-month' : 'other-month'}
                  onClick={() => (isCurrentMonth ? handleDayClick(day) : null)}
                >
                  {dayNumber}
                </td>
              );
            })}
          </tr>
        ))}
      </tbody>
    </table>
    <Modal isOpen={showForm} className="modal">
        <h2>Add Appointment</h2>
        <p>Date: {selectedDate && format(selectedDate, 'dd MMMM yyyy')}</p>
        <form onSubmit={handleSubmitForm}>
          <label>
            Patient Name:
            <input type="text" name="patientName" />
          </label>
          <button type="submit">Add</button>
          <button type="button" onClick={handleCloseForm}>Cancel</button>
        </form>
      </Modal>
  </div>
  );
};

export default CardCalendar;
