// GestionPatient.js
import React, { useContext, useState } from "react";
import { Redirect } from "react-router-dom";
import { AuthContext } from "contexts/AuthContext";
import CardPatients from "components/Cards/CardPatients";
import CardAddPatient from "components/Cards/CardAddPatient";
import CardProfilePatient from "components/Cards/CardProfilePatient"; // Make sure to import CardProfilePatient

export default function GestionPatient() {
  const { token } = useContext(AuthContext);
  const [isAddPatientOpen, setIsAddPatientOpen] = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null); // State for selected patient

  const handleOpenAddPatient = () => {
    setIsAddPatientOpen(true);
  };

  const handleCloseAddPatient = () => {
    setIsAddPatientOpen(false);
  };

  const handleAddPatientSuccess = () => {
    setIsAddPatientOpen(false);
  };

  const handleViewProfile = (patient) => {
    setSelectedPatient(patient);
  };

  const handleCloseProfile = () => {
    setSelectedPatient(null);
  };

  if (!token) {
    return <Redirect to="/auth/login" />;
  }

  return (
    <div className="flex flex-wrap mt-4">
      <div className="w-full mb-12 px-4">
        {isAddPatientOpen ? (
          <CardAddPatient
            onClose={handleCloseAddPatient}
            onAddSuccess={handleAddPatientSuccess}
          />
        ) : selectedPatient ? ( // Check if a patient is selected, render the profile card if true
          <CardProfilePatient patient={selectedPatient} onClose={handleCloseProfile} />
        ) : (
          <CardPatients color="light" onOpenAddPatient={handleOpenAddPatient} onViewProfile={handleViewProfile} />
        )}
      </div>
    </div>
  );
}
