import React, { useState } from "react";
import PropTypes from "prop-types";

export default function CardAddPatient({ isOpen, onClose }) {
  const [patientData, setPatientData] = useState({
    cin: "",
    nom: "",
    prenom: "",
    date_de_naissance: "",
    verifie: 0,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPatientData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can implement the API call to add a new patient
    console.log("Adding patient:", patientData);
    // After adding the patient, you can close the modal
    onClose();
  };

  return (
    <div
      className={`fixed top-0 left-0 w-full h-full flex items-center justify-center ${
        isOpen ? "block" : "hidden"
      }`}
    >
      <div className="absolute w-full h-full bg-gray-900 opacity-50"></div>
      <div className="bg-white w-96 rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Add a patient</h2>
        <form onSubmit={handleSubmit}>
          {/* Your form fields here */}
          <div className="mb-4">
            <label className="block font-medium mb-2">CIN</label>
            <input
              type="text"
              name="cin"
              value={patientData.cin}
              onChange={handleInputChange}
              className="border border-gray-300 px-3 py-2 rounded w-full"
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium mb-2">Nom</label>
            <input
              type="text"
              name="nom"
              value={patientData.nom}
              onChange={handleInputChange}
              className="border border-gray-300 px-3 py-2 rounded w-full"
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium mb-2">Pr√©nom</label>
            <input
              type="text"
              name="prenom"
              value={patientData.prenom}
              onChange={handleInputChange}
              className="border border-gray-300 px-3 py-2 rounded w-full"
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium mb-2">Date de naissance</label>
            <input
              type="text"
              name="date_de_naissance"
              value={patientData.date_de_naissance}
              onChange={handleInputChange}
              className="border border-gray-300 px-3 py-2 rounded w-full"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add
          </button>
        </form>
      </div>
    </div>
  );
}

CardAddPatient.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
};
