import axios from 'axios';

const getToken = () => {
  return localStorage.getItem('USER_KEY');
};

export const fetchUserData = async () => {
  try {
    const response = await axios.get(
      `${process.env.hostUrl || 'http://localhost:8080'}/api/v1/auth/userinfo`,
      {
        headers: {
          Authorization: 'Bearer ' + getToken(),
        },
      }
    );
    return response.data;
  } catch (error) {
    // Gérer les erreurs de manière appropriée (ex: redirection vers la page de connexion)
    throw error;
  }
};
