import React, { useState, useEffect, useContext } from "react";
import PropTypes from "prop-types";
import createApiInstance from "api/api";
import { AuthContext } from "contexts/AuthContext";
import CardAddPatient from "./CardAddPatient";

export default function CardPatients({ color }) {
  const { token } = useContext(AuthContext);
  const [searchQuery, setSearchQuery] = useState("");
  const [patients, setPatients] = useState([]);
  const [filteredPatients, setFilteredPatients] = useState([]);
  const apiInstance = createApiInstance(token);

  useEffect(() => {
    if (!token) {
      return;
    }

    apiInstance
      .get("api/patients")
      .then((response) => {
        setPatients(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [token]);

  useEffect(() => {
    setFilteredPatients(patients);
  }, [patients]);

  useEffect(() => {
    const query = searchQuery.toLowerCase();
    const filtered = patients.filter((patient) => {
      const { cin, nom, prenom } = patient;
      return (
        cin.toLowerCase().includes(query) ||
        nom.toLowerCase().includes(query) ||
        prenom.toLowerCase().includes(query)
      );
    });
    setFilteredPatients(filtered);
  }, [searchQuery, patients]);

  const handleDeletePatient = (patientId) => {
    const confirmDelete = window.confirm(
      "Êtes-vous sûr de vouloir supprimer ce patient ?"
    );
    if (confirmDelete) {
      apiInstance
        .delete(`/api/patients/${patientId}`)
        .then((response) => {
          console.log("Patient deleted successfully:", response.data);
          setFilteredPatients((prevPatients) =>
            prevPatients.filter((patient) => patient.id_patient !== patientId)
          );
        })
        .catch((error) => {
          console.error("Error deleting patient:", error);
        });
    }
  };

  const [isAddPatientOpen, setIsAddPatientOpen] = useState(false);

  const handleOpenAddPatient = () => {
    setIsAddPatientOpen(true);
  };

  const handleCloseAddPatient = () => {
    setIsAddPatientOpen(false);
  };

  return (
    <div>
      {isAddPatientOpen ? (
        <CardAddPatient onClose={handleCloseAddPatient} />
      ) : (
        <div
          className={
            "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
            (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
          }
        >
          <div className="rounded-t mb-0 px-4 py-3 border-0">
            <div className="flex flex-wrap items-center justify-between">
              <div className="relative w-full max-w-full flex-grow flex-1">
                <h3
                  className={
                    "font-semibold text-lg " +
                    (color === "light" ? "text-blueGray-700" : "text-white")
                  }
                >
                  Patients
                </h3>
              </div>
              <div className="relative flex items-center">
                <span className="z-10 h-full leading-snug font-normal absolute text-blueGray-300 bg-transparent rounded text-base items-center justify-center w-8 pl-3 py-3">
                  <i className="fas fa-search"></i>
                </span>
                <input
                  type="text"
                  placeholder="Chercher ici"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="border-0 px-3 py-3 pl-10 placeholder-blueGray-300 text-blueGray-600 relative bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring"
                />
              </div>
              <button
                onClick={handleOpenAddPatient}
                className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-md hover:bg-indigo-500 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-indigo-500"
              >
                <i className="fas fa-plus mr-2"></i> Add a patient
              </button>
            </div>
          </div>
          <div className="block w-full overflow-x-auto">
            <table className="items-center w-full bg-transparent border-collapse">
              {/* Rest of the table code */}
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

CardPatients.defaultProps = {
  color: "light",
};

CardPatients.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
