import React from "react";
import PropTypes from "prop-types";

export default function CardProfilePatient({ patient, onClose }) {
  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-xl rounded-lg">
        <div className="text-center mt-5">
          <img src={patient.photo_cin} alt="Patient's CIN" className="h-40 mx-auto mb-4 rounded-lg" />
          <h3 className="text-xl font-semibold leading-normal text-blueGray-700">
            {patient.nom} {patient.prenom}
          </h3>
        </div>
        <div className="flex flex-wrap py-4 text-center">
          <div className="relative w-full mb-3 text-blueGray-600 font-semibold">
            Patient N° <span className="italic">{patient.code_patient || "-"}</span>
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Type: {patient.type_patient || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            CIN: {patient.cin || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Vérifié: {patient.verifie === 0 && (
                      <i
                        className="fas fa-circle mr-2 text-lg"
                        style={{ color: "red", marginRight: "8px" }}
                      ></i>
                    )}
                    {patient.verifie === 1 && (
                      <i
                        className="fas fa-circle mr-2 text-lg"
                        style={{ color: "orange", marginRight: "8px" }}
                      ></i>
                    )}
                    {patient.verifie === 2 && (
                      <i
                        className="fas fa-circle mr-2 text-lg"
                        style={{ color: "green", marginRight: "8px" }}
                      ></i>
                    )}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Date de naissance: {patient.date_de_naissance || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Sexe: {patient.sexe || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Telephone: {patient.telephone || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Email: {patient.email || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Ville: {patient.ville || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Mutuelle: {patient.mutuelle || "-"}
          </div>
          <div className="relative w-full mb-3 text-blueGray-600 mt-3 lg:w-4/12">
            Lien de parenté: {patient.id_parent || "-"}
          </div>
        </div>
      </div>
    </>
  );
}

CardProfilePatient.propTypes = {
  patient: PropTypes.object.isRequired,
  onClose: PropTypes.func.isRequired,
};
