import React, { useState } from "react";
import axios from "axios";

export default function CardRegister() {
  const [showAlert, setShowAlert] = useState(false);
  const [alertType, setAlertType] = useState("success"); // 'success' ou 'error'
  const [alertMessage, setAlertMessage] = useState("");

  const [userData, setUserData] = useState({
    roles: [],
    username: "",
    password: "",
    email: "",
    nom: "",
    prenom: "",
    date_de_naissance: {
      year: "",
      month: "",
      day: "",
    },
    sexe: "",
    type_patient: "officiel",
    cin: "",
    photo_cin: "",
    telephone: "",
    ville: "",
    mutuelle: "CNSS",
    autre_mutuelle: "",
    caractere: "",
  });

  const handleRoleChange = (event) => {
    const { value, checked } = event.target;
    setUserData((prevData) => {
      if (checked) {
        return {
          ...prevData,
          roles: [...prevData.roles, value],
        };
      } else {
        return {
          ...prevData,
          roles: prevData.roles.filter((role) => role !== value),
        };
      }
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const date_de_naissance = `${userData.date_de_naissance.year}-${
      userData.date_de_naissance.month
    }-${userData.date_de_naissance.day}`;

    const postData = {
      ...userData,
      date_de_naissance,
    };

    axios
      .post("/api/patients/", postData)
      .then((response) => {
        console.log("Patient added successfully:", response.data);
        setShowAlert(true);
        setAlertType("success");
        setAlertMessage("Patient added successfully.");
      })
      .catch((error) => {
        console.error("Error adding patient:", error);
        setShowAlert(true);
        setAlertType("error");
        setAlertMessage("Error adding patient. Please try again.");
      });
  };

  const handleUpload = (event) => {
    // Handle the file upload here and update the photo_cin state with the file URL
    const file = event.target.files[0];
    // You can use FileReader or any file uploading library to handle the file upload
    // Example with FileReader:
    const reader = new FileReader();
    reader.onloadend = () => {
      setUserData({
        ...userData,
        photo_cin: reader.result,
      });
    };
    reader.readAsDataURL(file);
  };

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-100 border-0">
        {/* Rest of the JSX */}
        <div className="flex flex-wrap py-4">
          {/* Rest of the JSX */}
          <div className="w-full lg:w-6/12 px-4">
            <div className="relative w-full mb-3">
              <label
                className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                htmlFor="grid-password"
              >
                Date of Birth
              </label>
              <div className="flex space-x-2">
                <input
                  type="text"
                  name="day"
                  placeholder="Day"
                  value={userData.date_de_naissance.day}
                  onChange={(e) =>
                    handleChange({
                      target: { name: "date_de_naissance.day", value: e.target.value },
                    })
                  }
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                />
                <input
                  type="text"
                  name="month"
                  placeholder="Month"
                  value={userData.date_de_naissance.month}
                  onChange={(e) =>
                    handleChange({
                      target: { name: "date_de_naissance.month", value: e.target.value },
                    })
                  }
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                />
                <input
                  type="text"
                  name="year"
                  placeholder="Year"
                  value={userData.date_de_naissance.year}
                  onChange={(e) =>
                    handleChange({
                      target: { name: "date_de_naissance.year", value: e.target.value },
                    })
                  }
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                />
              </div>
            </div>
          </div>
          <div className="w-full lg:w-6/12 px-4">
            <div className="relative w-full mb-3">
              <label
                className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                htmlFor="grid-password"
              >
                Gender
              </label>
              <div className="space-x-4">
                <label>
                  <input
                    type="radio"
                    name="sexe"
                    value="Male"
                    checked={userData.sexe === "Male"}
                    onChange={handleChange}
                  />
                  Male
                </label>
                <label>
                  <input
                    type="radio"
                    name="sexe"
                    value="Female"
                    checked={userData.sexe === "Female"}
                    onChange={handleChange}
                  />
                  Female
                </label>
              </div>
            </div>
          </div>
          <div className="w-full lg:w-6/12 px-4">
            <div className="relative w-full mb-3">
              <label
                className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                htmlFor="grid-password"
              >
                Type Patient
              </label>
              <div className="space-x-4">
                <label>
                  <input
                    type="radio"
                    name="type_patient"
                    value="officiel"
                    checked={userData.type_patient === "officiel"}
                    onChange={handleChange}
                  />
                  Officiel
                </label>
                <label>
                  <input
                    type="radio"
                    name="type_patient"
                    value="provisoire"
                    checked={userData.type_patient === "provisoire"}
                    onChange={handleChange}
                  />
                  Provisoire
                </label>
              </div>
            </div>
          </div>
          <div className="w-full lg:w-6/12 px-4">
            <div className="relative w-full mb-3">
              <label
                className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                htmlFor="grid-password"
              >
                Photo CIN
              </label>
              <div className="flex items-center">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleUpload}
                  className="hidden"
                />
                <label
                  className="cursor-pointer mr-5"
                  htmlFor="photo_cin"
                >
                  <i className="fas fa-upload mr-2"></i>
                  Upload Photo
                </label>
                {userData.photo_cin && (
                  <img
                    src={userData.photo_cin}
                    alt="CIN Photo"
                    className="w-10 h-10 rounded-full"
                  />
                )}
              </div>
            </div>
          </div>
          <div className="w-full lg:w-6/12 px-4">
            <div className="relative w-full mb-3">
              <label
                className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                htmlFor="grid-password"
              >
                Mutuelle
              </label>
              <select
                name="mutuelle"
                value={userData.mutuelle}
                onChange={handleChange}
                className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
              >
                <option value="CNSS">CNSS</option>
                <option value="CNOPS">CNOPS</option>
                <option value="other">Other</option>
              </select>
              {userData.mutuelle === "other" && (
                <input
                  type="text"
                  name="autre_mutuelle"
                  placeholder="Enter Mutuelle Name"
                  value={userData.autre_mutuelle}
                  onChange={handleChange}
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150 mt-2"
                />
              )}
            </div>
          </div>
          {/* Rest of the JSX */}
        </div>
        {/* Rest of the JSX */}
      </div>
    </>
  );
}
