const CardCalendar = () => {
    const [currentDate, setCurrentDate] = useState(new Date());
    //const [selectedDate, setSelectedDate] = useState(null);
  //  const [showForm, setShowForm] = useState(false);
  
    const firstDay = dateFns.startOfMonth(currentDate);
    const lastDay = dateFns.lastDayOfMonth(currentDate);

    const startDate = dateFns.startOfWeek(currentDate);
    const endDate = dateFns.lastDayOfWeek(currentDate);
    
    const totalDate = dateFns.eachDayOfInterval({start: startDate , end: endDate});
    console.log(totalDate);




  return (
    <div className="calendar-container">
        <div>Month year</div>
        <div>
            <div>week</div>
            {totalDate.map(date => <span>{dateFns.format(date,)}</span>)}
        </div>
   
  </div>
  );
};

export default CardCalendar;
