import React, { useState } from 'react';
import { Calendar, momentLocalizer,Toolbar } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import EventForm from './EventForm';
import MyToolbar from './MyToolbar';

const localizer = momentLocalizer(moment);



const CardCalendar = () => {

    const [events, setEvents] = useState([
        {
          title: 'Rendez-vous 1',
          start: new Date(2023, 6, 25, 10, 0),
          end: new Date(2023, 6, 25, 11, 0),
        },
        {
          title: 'Rendez-vous 2',
          start: new Date(2023, 6, 25, 14, 0),
          end: new Date(2023, 6, 25, 15, 30),
        },
      ]);
    
      const handleAddEvent = (event) => {
        // Ajouter un nouvel événement à la liste des événements
        setEvents([...events, event]);
      };

      const [isModalOpen, setIsModalOpen] = useState(false);

 

  const handleSelectSlot = ({ start, end }) => {
    setIsModalOpen(true);
    setEventData({ start, end });
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEventData({ title: '', start: null, end: null });
  };

  return (
    <div>
              <EventForm onAddEvent={handleAddEvent} />

      <div style={{ height: 500 }}>
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          components={{
            toolbar: MyToolbar,
          }}
          onNavigate={(date) => console.log('Navigated to', date)}
        />
      </div>
    </div>
  );
};

export default CardCalendar;
