import React, { useContext, useEffect, useState } from "react";
import { Redirect } from "react-router-dom";
import createApiInstance from "api/api";
import { AuthContext } from "contexts/AuthContext";
import CardPatients from "components/Cards/CardPatients";
import CardAddPatient from "components/Cards/CardAddPatient"; // Import the CardAddPatient component

export default function GestionPatient() {
  const { token } = useContext(AuthContext);

  if (!token) {
    return <Redirect to="/auth/login" />;
  }

  // State to track whether the CardAddPatient modal is open or closed
  const [isAddPatientModalOpen, setIsAddPatientModalOpen] = useState(false);

  // Function to open the CardAddPatient modal
  const handleOpenAddPatientModal = () => {
    setIsAddPatientModalOpen(true);
  };

  // Function to close the CardAddPatient modal
  const handleCloseAddPatientModal = () => {
    setIsAddPatientModalOpen(false);
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full mb-12 px-4">
          {/* Render the CardPatients component */}
          <CardPatients color="light" patients={patients} />

          {/* Render the CardAddPatient modal conditionally */}
          <CardAddPatient
            isOpen={isAddPatientModalOpen}
            onClose={handleCloseAddPatientModal}
          />

          {/* Add a button to open the CardAddPatient modal */}
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded mt-4"
            onClick={handleOpenAddPatientModal}
          >
            <i className="fas fa-plus mr-2"></i>
            Add a patient
          </button>
        </div>
      </div>
    </>
  );
}
