import createApiInstance from "api/api";
import axios from "axios";
import { AuthContext } from "contexts/AuthContext";
import React, { useContext, useRef, useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

// components

export default function CardAddPatient() {
  const [showAlert, setShowAlert] = useState(false);
  const [alertType, setAlertType] = useState("success");
  const [alertMessage, setAlertMessage] = useState("");
  const [searchFieldValue, setSearchFieldValue] = useState("");
  const { token } = useContext(AuthContext);
  const apiInstance = createApiInstance(token);
  const [showSearchResults, setShowSearchResults] = useState(true);
  const [userData, setUserData] = useState({
    email: "",
    nom: "",
    prenom: "",
    sexe: "Male",
    type_patient: "provisoire",
    cin: "",
    photo_cin: "",
    telephone: "",
    ville: "",
    mutuelle: "Aucun",
    day: "",
    month: "",
    year: "",
    id_patient: "",
    date_de_naissance: "",
  });

  const combineDateInputs = (day, month, year) => {
    if (day && month && day !== 0 && month !== 0) {
      return `${day}-${month}-${year}`;
    } else {
      return `${year}`;
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === "day" || name === "month" || name === "year") {
      const { day, month, year } = userData;
      const formattedDate = combineDateInputs(
        name === "day" ? value : day,
        name === "month" ? value : month,
        name === "year" ? value : year
      );
      setUserData({
        ...userData,
        date_de_naissance: formattedDate,
        [name]: value,
      });
    } else {
      setUserData({
        ...userData,
        [name]: value,
      });
    }
  };

  const handleTypePatientChange = (event) => {
    setUserData({
      ...userData,
      type_patient: event.target.value,
    });
  };

  const handleSexeChange = (event) => {
    setUserData({
      ...userData,
      sexe: event.target.value,
    });
  };

  const fileInputRef = useRef(null);

  const handlePhotoCINUpload = () => {
    fileInputRef.current.click();
  };

  const handleFileInputChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUserData({
          ...userData,
          photo_cin: reader.result,
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleTakePhoto = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement("video");
      video.srcObject = mediaStream;
      video.onloadedmetadata = () => {
        const canvas = document.createElement("canvas");
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);
        const photoUrl = canvas.toDataURL();
        setUserData({
          ...userData,
          photo_cin: photoUrl,
        });
        mediaStream.getTracks().forEach((track) => track.stop());
      };
    } catch (error) {
      console.error("Error accessing camera:", error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  if (!userData.nom.trim() || !userData.prenom.trim()) {
    setShowAlert(true);
    setAlertType("error");
    setAlertMessage("Nom et prénom sont obligatoires !");
    return;
  }

  if (
    userData.day !== "" &&
    userData.month !== "" &&
    (userData.year === "" || userData.year === "0")
  ) {
    setShowAlert(true);
    setAlertType("error");
    setAlertMessage("Année obligatoire !");
    return;
  }
    apiInstance
      .post("/api/patients/", userData)
      .then((response) => {
        console.log("Patient added successfully:", response.data);
        setShowAlert(true);
        setAlertType("success");
        setAlertMessage("Patient added successfully.");
      })
      .catch((error) => {
        console.error("Error adding patient:", error);
        setShowAlert(true);
        setAlertType("error");
        setAlertMessage("Error adding patient. Please try again.");
      });
  };

  const [searchResults, setSearchResults] = useState([]);
  const [selectedFather, setSelectedFather] = useState(null);

  const handleSearchFather = (event) => {
    const { value } = event.target;
    setSearchFieldValue(value);
    apiInstance
      .get(`/api/patients?q=${value}`)
      .then((response) => {
        setSearchResults(response.data);
        setShowSearchResults(true);
      })
      .catch((error) => {
        console.error("Error searching for fathers:", error);
      });
  };

  const handleSelectFather = (father) => {
    setSelectedFather(father);
    setUserData({
      ...userData,
      id_parent: father.id_patient,
    });
    setSearchResults([]);
    setShowSearchResults(false);
    setSearchFieldValue(`{fat}`);
  };

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-100 border-0">
        <div className="rounded-t bg-white mb-0 px-4 py-3">
          <div className="text-center flex justify-between">
            <div className="lg:w-1/12">
              <h6 className="text-blueGray-700 text-xl font-bold">
                Ajouter un Patient
              </h6>
            </div>
          </div>
        </div>
        <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
          <form>
            <div className="flex flex-wrap py-4">
              <div className="w-full lg:w-4/12 px-4">
                <label
                  className="block uppercase text-blueGray-600 text-xs font-bold mb-4"
                  htmlFor="type_patient"
                >
                  Type de Patient
                </label>
                <div>
                  <label className="mr-2">
                    <input
                      type="radio"
                      name="type_patient"
                      value="officiel"
                      checked={userData.type_patient === "officiel"}
                      onChange={handleTypePatientChange}
                    />
                    Officiel
                  </label>
                  <label>
                    <input
                      type="radio"
                      name="type_patient"
                      value="provisoire"
                      checked={userData.type_patient === "provisoire"}
                      onChange={handleTypePatientChange}
                    />
                    Provisoire
                  </label>
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="nom"
                  >
                    Nom
                  </label>
                  <input
                    type="text"
                    onChange={handleChange}
                    name="nom"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="prenom"
                  >
                    Prénom
                  </label>
                  <input
                    type="text"
                    name="prenom"
                    onChange={handleChange}
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="cin"
                  >
                    CIN
                  </label>
                  <div className="flex items-center">
                    <input
                      type="text"
                      name="cin"
                      onChange={handleChange}
                      className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring lg:w-8/12 ease-linear transition-all duration-150"
                      required
                    />
                    <div className="flex ml-2">
                      <div
                        className="clickable-icon"
                        onClick={handlePhotoCINUpload}
                      >
                        <i className="fas fa-upload px-4"></i>
                      </div>
                      <div
                        className="clickable-icon ml-2"
                        onClick={handleTakePhoto}
                      >
                        <i className="fas fa-camera"></i>
                      </div>
                    </div>
                  </div>
                  <input
                    type="file"
                    name="photo_cin"
                    onChange={handleFileInputChange}
                    ref={fileInputRef}
                    className="hidden"
                    accept="image/*"
                    capture="user"
                    required
                  />
                  {userData.photo_cin && (
                    <img
                      src={userData.photo_cin}
                      alt="CIN"
                      className="mt-2 rounded-md h-20"
                    />
                  )}
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <label
                  className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                  htmlFor="date_de_naissance"
                >
                  Date de naissance
                </label>
                <div className="flex items-center">
                  <input
                    type="number"
                    name="day"
                    placeholder="Jour"
                    value={userData.day}
                    onChange={handleChange}
                    className="border-0 px-3 py-3 text-center placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-16 md:w-10 lg:w-14 xl:w-16 ease-linear transition-all duration-150 mr-1"
                    required
                  />
                  <input
                    type="number"
                    name="month"
                    placeholder="Moi"
                    value={userData.month}
                    onChange={handleChange}
                    className="border-0 px-3 py-3 text-center placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-16 md:w-10 lg:w-14 xl:w-16 ease-linear transition-all duration-150 ml-1 mr-1"
                    required
                  />
                  <input
                    type="number"
                    name="year"
                    placeholder="Année"
                    value={userData.year}
                    onChange={handleChange}
                    className="border-0 px-3 py-3 text-center placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-24 md:w-20 lg:w-24 xl:w-28 ease-linear transition-all duration-150 ml-1"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <label
                  className="block uppercase text-blueGray-600 text-xs font-bold mb-4"
                  htmlFor="sexe"
                >
                  Genre
                </label>
                <div>
                  <label className="mr-2">
                    <input
                      type="radio"
                      name="sexe"
                      value="Male"
                      checked={userData.sexe === "Male"}
                      onChange={handleSexeChange}
                    />
                    Homme
                  </label>
                  <label>
                    <input
                      type="radio"
                      name="sexe"
                      value="Female"
                      checked={userData.sexe === "Female"}
                      onChange={handleSexeChange}
                    />
                    Femme
                  </label>
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="telephone"
                  >
                    Téléphone
                  </label>
                  <input
                    type="tel"
                    name="telephone"
                    onChange={handleChange}
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="email"
                  >
                    Email
                  </label>
                  <input
                    type="email"
                    name="email"
                    onChange={handleChange}
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="ville"
                  >
                    Ville
                  </label>
                  <input
                    type="text"
                    name="ville"
                    onChange={handleChange}
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    required
                  />
                </div>
              </div>
              <div className="w-full lg:w-4/12 px-4">
                <label
                  className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                  htmlFor="mutuelle"
                >
                  Mutuelle
                </label>
                <select
                  name="mutuelle"
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                  required
                >
                  <option value="other">Aucun</option>
                  <option value="CNSS">CNSS</option>
                  <option value="CNOPS">CNOPS</option>
                </select>
              </div>
              <div className="w-full lg:w-4/12 px-4">
              <div className="relative w-full mb-3">
                <label
                  className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                  htmlFor="father_search"
                >
                  Rechercher le Père
                </label>
                <input
                  type="text"
                  name="father_search"
                  onChange={handleSearchFather}
                  value={searchFieldValue}
                  className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                />
                {showSearchResults && searchResults.length > 0 && (
                <div className="mt-2 border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150">
                  {searchResults.map((father) => (
                    <div
                      key={father.id_patient}
                      onClick={() => handleSelectFather(father)}
                    >
                      {`${father.nom} ${father.prenom}`}
                    </div>
                  ))}
                </div>
              )}
              </div>
            </div>
            </div>
            <button
              className="bg-lightBlue-500 text-white active:bg-lightBlue-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 w-full ease-linear transition-all duration-150"
              type="submit"
              onClick={handleSubmit}
            >
              Ajouter
            </button>

            {showAlert && (
              <div
                className={`${
                  alertType === "success"
                    ? "bg-green-200 text-green-700"
                    : "bg-red-200 text-red-700"
                } border-l-4 border-green-700 p-4 mt-4`}
              >
                {alertMessage}
              </div>
            )}
          </form>
        </div>
      </div>
    </>
  );
}
