import React, { useContext, useEffect, useState } from "react";
import PropTypes from "prop-types";

// components

import TableDropdown from "components/Dropdowns/TableDropdown.js";
import { AuthContext } from "contexts/AuthContext";
import createApiInstance from "api/api";

export default function CardAfficherUser({ color }) {
    const { token } = useContext(AuthContext);
    const [userInfo, setUserInfo] = useState([]);
    const apiInstance = createApiInstance(token);

    useEffect(() => {
        apiInstance
          .get("/api/v1/users")
          .then((response) => {
            if (response.data.userName && Array.isArray(response.data.roles)) {
              // Format the response into an array of a single object
              const formattedData = [
                {
                  id: response.data.userName,
                  roles: response.data.roles,
                },
              ];
              setUserInfo(formattedData);
            } else {
              console.error("Invalid response format: ", response.data);
            }
          })
          .catch((error) => {
            console.error(error);
          });
      }, []);

  return (
    <>
      <div
        className={
          "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
          (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
        }
      >
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
              <h3
                className={
                  "font-semibold text-lg " +
                  (color === "light" ? "text-blueGray-700" : "text-white")
                }
              >
                Les utilisateurs
              </h3>
            </div>
          </div>
        </div>
        <div className="block w-full overflow-x-auto">
          {/* Projects table */}
          <table className="items-center w-full bg-transparent border-collapse">
            <thead>
              <tr>
                <th
                  className={
                    "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  Username
                </th>
                <th
                  className={
                    "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  Email
                </th>
                <th
                  className={
                    "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  Actions
                </th>
                
                
                
               
              </tr>
            </thead>
            <tbody>
              <tr>
                
               
                
                
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                  <div className="flex items-center">
                    test
                  </div>
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                  <div className="flex items-center">
                    test
                  </div>
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                  <div className="flex items-center">
                    test
                  </div>
                </td>
                
              </tr>
              
             
              
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

CardAfficherUser.defaultProps = {
  color: "light",
};

CardAfficherUser.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
