import React, { useState } from 'react';
import * as dateFns from "date-fns"
import "./Card.css"

const formatOfDay = 'd';
const formatOfYear = 'yyy';
const formatOfMonth = 'MMM';
const formatOfWeek = 'eee';


const CardCalendar = () => {
    const [currentDate, setCurrentDate] = useState(new Date());
    //const [selectedDate, setSelectedDate] = useState(null);
  //  const [showForm, setShowForm] = useState(false);
  
    const firstDay = dateFns.startOfMonth(currentDate);
    const lastDay = dateFns.lastDayOfMonth(currentDate);

    const startDate = dateFns.startOfWeek(currentDate);
    const endDate = dateFns.lastDayOfWeek(currentDate);
    
    const totalDate = dateFns.eachDayOfInterval({start: startDate , end: endDate});
    console.log(totalDate);

    const weeks = (dates => {
        const weeks = [];
        let week = [];
        dates.forEach((date, index) => {
          week.push(date);
          if ((index + 1) % 7 === 0) {
            weeks.push(week);
            week = [];
          }
        });
        return weeks;
      })(totalDate);


  return (
    <div className="">
        <div>Month year</div>
        <div style={{display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: '1rem'}}>
            {weeks.map(week => (<span>{dateFns.format(week,formatOfWeek)}</span>))}
            {totalDate.map(date => (<span>{dateFns.format(date,formatOfDay)}</span>))}
        </div>
   
  </div>
  );
};

export default CardCalendar;
